//SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

interface IERC20 {
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
}

error AccessDenied();
error NotFound();
error AlreadyRevoked();
error Irrevocable();


// Use openzeppelin to inherit battle-tested implementations (ERC20, ERC721, etc)
// import "@openzeppelin/contracts/access/Ownable.sol";
// Events: a way to emit log statements from smart contract that can be listened to by external parties
// Constructor: Called once on contract deployment
// Check packages/hardhat/deploy/00_deploy_your_contract.ts

contract Attesting {
    uint256 constant EMPTY_UID = 0;
    uint64 constant NO_EXPIRATION_TIME = 0;
	address public immutable owner;

    /// @notice Emitted when an attestation has been made.
    /// @param recipient The recipient of the attestation.
    /// @param attester The attesting account.
    /// @param uid The UID the revoked attestation.

    event Attested(address indexed attester, string indexed recipient, uint256 uid);

    /// @notice Emitted when an attestation has been revoked.
    /// @param recipient The recipient of the attestation.
    /// @param attester The attesting account.
    /// @param uid The UID the revoked attestation.
    event Revoked(string indexed recipient, address indexed attester, uint256 uid);

    struct Attestation {
        uint256 uid; // A unique identifier of the attestation.
        uint256 time; // The time when the attestation was created (Unix timestamp).
        uint256 revocationTime; // The time when the attestation was revoked (Unix timestamp).
        string recipient; // The recipient of the attestation.
        address attester; // The attester/sender of the attestation.
        bool revocable; // Whether the attestation is revocable.
        string data; // Custom attestation data.
        uint256 rating; // Rating of the attestation (1-5 stars)
    }

    // The global mapping between attestations and their UIDs.
    uint256 private lastUid = 1;

    // mapping(uint256 uid => Attestation attestation) private _db;
    mapping(uint256 => Attestation) private _db;

    /// @dev Creates a new SolAttest instance.
    constructor(address _owner) {
	owner = _owner;
	}

    /// @notice Attests to a specific schema.
    /// @param recipient The recipient of the attestation.
    /// @param revocable Whether the attestation is revocable.
    /// @param data The custom attestation data.
    /// @return The UID of the new attestation.
    function attest(string memory recipient, bool revocable, string calldata data, uint256 rating) external returns (uint256) {
        uint256 _uid = lastUid++;
        Attestation memory _attestation;

        _attestation.uid = _uid;
        _attestation.attester = msg.sender;
        _attestation.recipient = recipient;
        _attestation.rating = rating;
        _attestation.data = data;
        _attestation.time = block.timestamp;
        _attestation.revocable = revocable;

        _db[_uid] = _attestation;

        emit Attested(msg.sender, recipient, _uid);

        return _uid;
    }

        // Function to attest with token backing, sending tokens to a specified address
    function attestWithToken(
        string memory recipient, 
        bool revocable, 
        string calldata data, 
        address tokenAddress, 
        uint256 tokenAmount, 
        address payable destinationAddress // Address to send the tokens to
    ) external returns (uint256) {
        // Token transfer logic
        require(
            IERC20(tokenAddress).transferFrom(msg.sender, destinationAddress, tokenAmount), 
            "Token transfer failed"
        );

        // Attestation logic
        uint256 _uid = lastUid++;
        Attestation memory _attestation;

        _attestation.uid = _uid;
        _attestation.attester = msg.sender;
        _attestation.recipient = recipient;
        _attestation.data = data;
        _attestation.time = block.timestamp;
        _attestation.revocable = revocable;

        _db[_uid] = _attestation;

        emit Attested(msg.sender, recipient, _uid);

        return _uid;
    }


    function revoke(uint256 uid) external {
        Attestation storage attestation = _db[uid];

        if (attestation.uid == 0) {
            revert NotFound();
        }

        if (attestation.attester != msg.sender) {
            revert AccessDenied();
        }

        if (attestation.revocationTime != 0) {
            revert AlreadyRevoked();
        }

        if (!attestation.revocable) {
            revert Irrevocable();
        }

        attestation.revocationTime = block.timestamp;
    }

    /// @notice Returns an existing attestation by UID.
    /// @param uid The UID of the attestation to retrieve.
    /// @return The attestation data members.
    function getAttestation(uint256 uid) external view returns (Attestation memory) {
        if (uid > lastUid) {
            revert NotFound();
        }
        return _db[uid];
    }

    function attestationsByRecipient(string memory recipient) external view returns (Attestation[] memory) {
        uint256 count = 0;
        for (uint256 i = 1; i < lastUid; i++) {
            if (keccak256(abi.encodePacked(_db[i].recipient)) == keccak256(abi.encodePacked(recipient))) {
                count++;
            }
        }

        Attestation[] memory result = new Attestation[](count);
        uint256 index = 0;
        for (uint256 i = 1; i < lastUid; i++) {
            if (keccak256(abi.encodePacked(_db[i].recipient)) == keccak256(abi.encodePacked(recipient))) {
                result[index] = _db[i];
                index++;
            }
        }

        return result;
    }

    function attestationsByAttester(address attester) external view returns (Attestation[] memory) {
        uint256 count = 0;
        for (uint256 i = 1; i < lastUid; i++) {
            if (_db[i].attester == attester) {
                count++;
            }
        }

        Attestation[] memory result = new Attestation[](count);
        uint256 index = 0;
        for (uint256 i = 1; i < lastUid; i++) {
            if (_db[i].attester == attester) {
                result[index] = _db[i];
                index++;
            }
        }

        return result;
    }
    

}

contracts/payoutStrategy/IPayoutStrategy.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import "../utils/MetaPtr.sol";
import "../round/RoundImplementation.sol";

/**
 * @notice Defines the abstract contract for payout strategies
 * for a round. Any new payout strategy would be expected to
 * extend this abstract contract.
 *
 * Every PayoutStrategyImplementation contract would be unique to RoundImplementation
 * and would be deployed before creating a round.
 *
 * @dev
 *  - Deployed before creating a round
 *  - Funds are transferred to the payout contract from round only during payout
 */
abstract contract IPayoutStrategy {

  // --- Constants ---

  /// @notice round operator role
  bytes32 public constant ROUND_OPERATOR_ROLE = keccak256("ROUND_OPERATOR");

  // --- Data ---

  /// @notice RoundImplementation address
  address payable public roundAddress;

  /// @notice Token address
  address public tokenAddress;

  // @notice
  bool public isReadyForPayout;

  // --- Event ---

  /// @notice Emitted when contract is ready for payout
  event ReadyForPayout();

  // --- Modifier ---

  /// @notice modifier to check if sender is round contract.
  modifier isRoundContract() {
    require(roundAddress != address(0), "not linked to a round");
    require(msg.sender == roundAddress, "not invoked by round");
    _;
  }

  /// @notice modifier to check if sender is round operator.
  modifier isRoundOperator() {
    require(
      RoundImplementation(roundAddress).hasRole(ROUND_OPERATOR_ROLE, msg.sender),
      "not round operator"
    );
    _;
  }

  /// @notice modifier to check if round has ended.
  modifier roundHasEnded() {
    uint roundEndTime = RoundImplementation(roundAddress).roundEndTime();
    require(block.timestamp > roundEndTime, "round has not ended");
    _;
  }

  // --- Core Methods ---

  /**
   * @notice Invoked by RoundImplementation on creation to
   * set the round for which the payout strategy is to be used
   */
  function init() external virtual {
    require(roundAddress == address(0x0), "roundAddress already set");
    roundAddress = payable(msg.sender);

    // set the token address
    tokenAddress = RoundImplementation(roundAddress).token();

    isReadyForPayout = false;
  }

  /// @notice Invoked by RoundImplementation to set isReadyForPayout
  /// @dev Can only be called once and (by default) cannot be changed once called
  function setReadyForPayout() virtual external payable isRoundContract roundHasEnded {
    require(isReadyForPayout == false, "isReadyForPayout already set");

    isReadyForPayout = true;
    emit ReadyForPayout();
  }

  receive() external payable {}
}

// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

interface IPayoutStrategyFactory {
    function initialize() external;

    function updatePayoutImplementation(
        address payable newPayoutImplementation
    ) external;

    function create() external returns (address);
}

contracts/payoutStrategy/Merkle/MerklePa.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

import "./MerklePayoutStrategyImplementation.sol";
import "@openzeppelin/contracts-upgradeable/proxy/ClonesUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";

import "../IPayoutStrategyFactory.sol";
import "../../utils/MetaPtr.sol";

contract MerklePayoutStrategyFactory is IPayoutStrategyFactory, OwnableUpgradeable {

  // --- Data ---

  address payable public payoutImplementation;

  uint256 public nonce;

  // --- Event ---

  /// @notice Emitted when payoutImplementation is updated
  event PayoutImplementationUpdated(address merklePayoutStrategyAddress);

  /// @notice Emitted when a new payout contract is created
  event PayoutContractCreated(
    address indexed payoutContractAddress,
    address indexed payoutImplementation
  );

  /// @notice constructor function which ensure deployer is set as owner
  function initialize() external initializer {
    __Context_init_unchained();
    __Ownable_init_unchained();
  }

  // --- Core methods ---

  /**
   * @notice Allows the owner to update the payoutImplementation.
   * This provides us the flexibility to upgrade MerklePayoutStrategyImplementation
   * contract while relying on the same MerklePayoutStrategyFactory to get the list of
   * MerklePayout contracts.
   *
   * @param newPayoutImplementation - address of the new payoutImplementation
   */
  function updatePayoutImplementation(address payable newPayoutImplementation) external onlyOwner {

    payoutImplementation = newPayoutImplementation;

    emit PayoutImplementationUpdated(newPayoutImplementation);
  }

  /**
   * @notice Clones MerklePayoutStrategyImplementation and deploys a contract
   * and emits an event
   */
  function create(
  ) external returns (address) {

    nonce++;

    bytes32 salt = keccak256(abi.encodePacked(msg.sender, nonce));
    address clone = ClonesUpgradeable.cloneDeterministic(payoutImplementation, salt);

    MerklePayoutStrategyImplementation(payable(clone)).initialize();
    emit PayoutContractCreated(clone, payoutImplementation);

    return clone;
  }

}

// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity 0.8.19;

import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
import "../../utils/MetaPtr.sol";
import "../IPayoutStrategy.sol";

/**
 * @notice Merkle Payout Strategy contract which is deployed once per round
 * and is used to upload the final match distribution.
 *
 */
contract MerklePayoutStrategyImplementation is IPayoutStrategy, Initializable {

  using SafeERC20Upgradeable for IERC20Upgradeable;

  string public constant VERSION = "0.2.0";

  using SafeERC20 for IERC20;

  // --- Data ---

  /// @notice merkle root generated from distribution
  bytes32 public merkleRoot;

  /// @notice packed array of booleans to keep track of claims
  mapping(uint256 => uint256) private distributedBitMap;

  /// MetaPtr containing the distribution
  MetaPtr public distributionMetaPtr;

  // --- Events ---

  /// @notice Emitted when funds are withdrawn from the payout contract
  event FundsWithdrawn(address indexed tokenAddress, uint256 amount, address withdrawAddress);

  /// @notice Emitted when the distribution is updated
  event DistributionUpdated(bytes32 merkleRoot, MetaPtr distributionMetaPtr);

  /// @notice Emitted when funds are distributed
  event FundsDistributed(
    uint256 amount,
    address grantee,
    address indexed token,
    bytes32 indexed projectId
  );

  /// @notice Emitted when batch payout is successful
  event BatchPayoutSuccessful(address indexed sender);

  // --- Types ---
  struct Distribution {
    uint256 index;
    address grantee;
    uint256 amount;
    bytes32[] merkleProof;
    bytes32 projectId;
  }

  // @NOTE: we need this because we're inheriting from Initializable.sol
  function initialize() external initializer {
    // empty initializer
  }

  // --- Core methods ---

  /// @notice Invoked by round operator to update the merkle root and distribution MetaPtr
  /// @param encodedDistribution encoded distribution
  function updateDistribution(bytes calldata encodedDistribution) external roundHasEnded isRoundOperator {

    require(isReadyForPayout == false, "Payout: Already ready for payout");

    (bytes32 _merkleRoot, MetaPtr memory _distributionMetaPtr) = abi.decode(
      encodedDistribution,
      (bytes32, MetaPtr)
    );

    merkleRoot = _merkleRoot;
    distributionMetaPtr = _distributionMetaPtr;

    emit DistributionUpdated(merkleRoot, distributionMetaPtr);
  }

  /// @notice function to check if distribution is set
  function isDistributionSet() public view returns (bool) {
    return merkleRoot != "";
  }

  /// @notice Invoked by RoundImplementation to set isReadyForPayout
  function setReadyForPayout() override external payable isRoundContract roundHasEnded {
    require(isReadyForPayout == false, "isReadyForPayout already set");
    require(isDistributionSet(), "distribution not set");

    isReadyForPayout = true;
    emit ReadyForPayout();
  }

  /// @notice Util function to check if distribution is done
  /// @param _index index of the distribution
  function hasBeenDistributed(uint256 _index) public view returns (bool) {

    uint256 distributedWordIndex = _index / 256;
    uint256 distributedBitIndex = _index % 256;
    uint256 distributedWord = distributedBitMap[distributedWordIndex];
    uint256 mask = (1 << distributedBitIndex);

    return distributedWord & mask == mask;
  }

  /**
   * @notice Invoked by RoundImplementation to withdraw funds to
   * withdrawAddress from the payout contract
   *
   * @param withdrawAddress withdraw funds address
   */
  function withdrawFunds(address payable withdrawAddress) external payable isRoundOperator roundHasEnded {

    uint balance = _getTokenBalance();

    if (tokenAddress == address(0)) {
      /// @dev native token
      AddressUpgradeable.sendValue(
        withdrawAddress,
        balance
      );
    } else {
      /// @dev ERC20 token
      IERC20Upgradeable(tokenAddress).safeTransfer(
        withdrawAddress,
        balance
      );
    }

    emit FundsWithdrawn(tokenAddress, balance, withdrawAddress);
  }

  /// @notice function to distribute funds to recipient
  /// @dev can be invoked only by round operator
  /// @param _distributions encoded distribution
  function payout(Distribution[] calldata _distributions) external payable isRoundOperator {
    require(isReadyForPayout == true, "Payout: Not ready for payout");

    for (uint256 i = 0; i < _distributions.length; ++i) {
      _distribute(_distributions[i]);
    }

    emit BatchPayoutSuccessful(msg.sender);
  }

  /// @notice Util function to distribute funds to recipient
  /// @param _distribution encoded distribution
  function _distribute(Distribution calldata _distribution) private {
    uint256 _index = _distribution.index;
    address _grantee = _distribution.grantee;
    uint256 _amount = _distribution.amount;
    bytes32 _projectId = _distribution.projectId;
    bytes32[] memory _merkleProof = _distribution.merkleProof;

    require(!hasBeenDistributed(_index), "Payout: Already distributed");

    /* We need double hashing to prevent second preimage attacks */
    bytes32 node = keccak256(bytes.concat(keccak256(abi.encode(_index, _grantee, _amount, _projectId))));

    require(MerkleProof.verify(_merkleProof, merkleRoot, node), "Payout: Invalid proof");

    _setDistributed(_index);

    _transferAmount(payable(_grantee), _amount);

    emit FundsDistributed(
      _amount,
      _grantee,
      tokenAddress,
      _projectId
    );
  }

  /// @notice Util function to mark distribution as done
  /// @param _index index of the distribution
  function _setDistributed(uint256 _index) private {
    uint256 distributedWordIndex = _index / 256;
    uint256 distributedBitIndex = _index % 256;
    distributedBitMap[distributedWordIndex] |= (1 << distributedBitIndex);
  }

  /// @notice Util function to transfer amount to recipient
  /// @param _recipient recipient address
  /// @param _amount amount to transfer
  function _transferAmount(address payable _recipient, uint256 _amount) private {
    if (tokenAddress == address(0)) {
      Address.sendValue(_recipient, _amount);
    } else {
      IERC20(tokenAddress).safeTransfer(_recipient, _amount);
    }
  }

  /**
   * Util function to get token balance in the contract
   */
  function _getTokenBalance() internal view returns (uint) {
    if (tokenAddress == address(0)) {
      return address(this).balance;
    } else {
      return IERC20Upgradeable(tokenAddress).balanceOf(address(this));
    }
  }
}

contracts/program/ProgramFactory.sol.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/ClonesUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../utils/MetaPtr.sol";

import "./ProgramImplementation.sol";

contract ProgramFactory is OwnableUpgradeable {
 
  address public programContract;

  // --- Event ---

  /// @notice Emitted when a Program contract is updated
  event ProgramContractUpdated(address programContractAddress);

  /// @notice Emitted when a new Program is created
  event ProgramCreated(address indexed programContractAddress, address indexed programImplementation);


  /// @notice constructor function which ensure deployer is set as owner
  function initialize() external initializer {
    __Context_init_unchained();
    __Ownable_init_unchained();
  }

  // --- Core methods ---

  /**
   * @notice Allows the owner to update the ProgramImplementation.
   * This provides us the flexibility to upgrade ProgramImplementation
   * contract while relying on the same ProgramFactory to get the list of
   * programs.
   */
  function updateProgramContract(address newProgramContract) external onlyOwner {
    // slither-disable-next-line missing-zero-check
    programContract = newProgramContract;

    emit ProgramContractUpdated(newProgramContract);
  }

  /**
   * @notice Clones ProgramImplmentation and deployed a program and emits an event
   *
   * @param encodedParameters Encoded parameters for creating a program
   */
  function create(
    bytes calldata encodedParameters
  ) external returns (address) {

    address clone = ClonesUpgradeable.clone(programContract);
    emit ProgramCreated(clone, programContract);
    ProgramImplementation(clone).initialize(encodedParameters);

    return clone;
  }
}

contracts/program/ProgramImplementation..content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

import "../utils/MetaPtr.sol";


/**
 * @notice Program which would managed by a group of 
 * PROGRAM_OPERATOR deployed via the ProgramFactory
 */
contract ProgramImplementation is AccessControlEnumerable, Initializable {

  // --- Libraries ---
  using Address for address;

  // --- Roles ---

  /// @notice program operator role
  bytes32 public constant PROGRAM_OPERATOR_ROLE = keccak256("PROGRAM_OPERATOR");

  // --- Events ---

  /// @notice Emitted when a team metadata pointer is updated
  event MetaPtrUpdated(MetaPtr oldMetaPtr, MetaPtr newMetaPtr);

  // --- Data ---

  /// @notice URL pointing for program metadata (for off-chain use)
  MetaPtr public metaPtr;


  // --- Core methods ---

  /**
   * @notice Instantiates a new program
   * @param encodedParameters Encoded parameters for program creation
   * @dev encodedParameters
   *  - _metaPtr URL pointing to the program metadata
   *  - _adminRoles Addresses to be granted DEFAULT_ADMIN_ROLE
   *  - _programOperators Addresses to be granted PROGRAM_OPERATOR_ROLE
   */
  function initialize(
    bytes calldata encodedParameters
  ) external initializer {
  
    // Decode _encodedParameters
    (
      MetaPtr memory _metaPtr,
      address[] memory _adminRoles,
      address[] memory _programOperators
    ) = abi.decode(
      encodedParameters, (
      MetaPtr,
      address[],
      address[]
    ));

    // Emit MetaPtrUpdated event for indexing
    emit MetaPtrUpdated(metaPtr, _metaPtr);
    metaPtr = _metaPtr;

    // Assigning default admin role
    for (uint256 i = 0; i < _adminRoles.length; ++i) {
      _grantRole(DEFAULT_ADMIN_ROLE, _adminRoles[i]);
    }

    // Assigning program operators
    for (uint256 i = 0; i < _programOperators.length; ++i) {
      _grantRole(PROGRAM_OPERATOR_ROLE, _programOperators[i]);
    }
  }

  // @notice Update metaPtr (only by PROGRAM_OPERATOR_ROLE)
  /// @param newMetaPtr new metaPtr
  function updateMetaPtr(MetaPtr memory newMetaPtr) external onlyRole(PROGRAM_OPERATOR_ROLE) {
    emit MetaPtrUpdated(metaPtr, newMetaPtr);
    metaPtr = newMetaPtr;
  }
}

contracts/projectRegistry/ProjectRegistr.content:
// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "../utils/MetaPtr.sol";

/**
 * @title ProjectRegistry
 */
contract ProjectRegistry is Initializable {
    // Types

    // The project structs contain the minimal data we need for a project
    struct Project {
        uint256 id;
        MetaPtr metadata;
    }

    // A linked list of owners of a project
    // The use of a linked list allows us to easily add and remove owners,
    // access them directly in O(1), and loop through them.
    //
    // {
    //     count: 3,
    //     list: {
    //         OWNERS_LIST_SENTINEL => owner1Address,
    //         owner1Address => owner2Address,
    //         owner2Address => owner3Address,
    //         owner3Address => OWNERS_LIST_SENTINEL
    //     }
    // }
    struct OwnerList {
        uint256 count;
        mapping(address => address) list;
    }

    // State variables

    // Used as sentinel value in the owners linked list.
    address constant OWNERS_LIST_SENTINEL = address(0x1);

    // The number of projects created, used to give an incremental id to each one
    uint256 public projectsCount;

    // The mapping of projects, from projectID to Project
    mapping(uint256 => Project) public projects;

    // The mapping projects owners, from projectID to OwnerList
    mapping(uint256 => OwnerList) public projectsOwners;

    // Events

    event ProjectCreated(uint256 indexed projectID, address indexed owner);
    event MetadataUpdated(uint256 indexed projectID, MetaPtr metaPtr);
    event OwnerAdded(uint256 indexed projectID, address indexed owner);
    event OwnerRemoved(uint256 indexed projectID, address indexed owner);

    // Modifiers

    modifier onlyProjectOwner(uint256 projectID) {
        require(projectsOwners[projectID].list[msg.sender] != address(0), "PR000");
        _;
    }

    /**
     * @notice Initializes the contract after an upgrade
     * @dev In future deploys of the implementation, a higher version should be passed to reinitializer
     */
    function initialize() public reinitializer(1) {
    }

    // External functions

    /**
     * @notice Creates a new project with a metadata pointer
     * @param metadata the metadata pointer
     */
    function createProject(MetaPtr calldata metadata) external {
        uint256 projectID = projectsCount++;

        Project storage project = projects[projectID];
        project.id = projectID;
        project.metadata = metadata;

        initProjectOwners(projectID);

        emit ProjectCreated(projectID, msg.sender);
        emit MetadataUpdated(projectID, metadata);
    }

    /**
     * @notice Updates Metadata for single project
     * @param projectID ID of previously created project
     * @param metadata Updated pointer to external metadata
     */
    function updateProjectMetadata(uint256 projectID, MetaPtr calldata metadata) external onlyProjectOwner(projectID) {
        projects[projectID].metadata = metadata;
        emit MetadataUpdated(projectID, metadata);
    }

    /**
     * @notice Associate a new owner with a project
     * @param projectID ID of previously created project
     * @param newOwner address of new project owner
     */
    function addProjectOwner(uint256 projectID, address newOwner) external onlyProjectOwner(projectID) {
        require(newOwner != address(0) && newOwner != OWNERS_LIST_SENTINEL && newOwner != address(this), "PR001");

        OwnerList storage owners = projectsOwners[projectID];

        require(owners.list[newOwner] == address(0), "PR002");

        owners.list[newOwner] = owners.list[OWNERS_LIST_SENTINEL];
        owners.list[OWNERS_LIST_SENTINEL] = newOwner;
        owners.count++;

        emit OwnerAdded(projectID, newOwner);
    }

    /**
     * @notice Disassociate an existing owner from a project
     * @param projectID ID of previously created project
     * @param prevOwner Address of previous owner in OwnerList
     * @param owner Address of new Owner
     */
    function removeProjectOwner(uint256 projectID, address prevOwner, address owner) external onlyProjectOwner(projectID) {
        require(owner != address(0) && owner != OWNERS_LIST_SENTINEL, "PR001");

        OwnerList storage owners = projectsOwners[projectID];

        require(owners.list[prevOwner] == owner, "PR003");
        require(owners.count > 1, "PR004");

        owners.list[prevOwner] = owners.list[owner];
        delete owners.list[owner];
        owners.count--;

        emit OwnerRemoved(projectID, owner);
    }

    // Public functions

    /**
     * @notice Returns all projects
     * @return allProjects Array of all projects
     */
    function getAllProjects() external view returns (Project[] memory) {
        Project[] memory allProjects = new Project[](projectsCount);
        for (uint256 i = 0; i < projectsCount; i++) {
            allProjects[i] = projects[i];
        }
        return allProjects;
    }

    /**
     * @notice Retrieve count of existing project owners
     * @param projectID ID of project 
     * @return Count of owners for given project
     */
    function projectOwnersCount(uint256 projectID) external view returns(uint256) {
        return projectsOwners[projectID].count;
    }

    /**
     * @notice Retrieve list of project owners 
     * @param projectID ID of project 
     * @return List of current owners of given project
     */
    function getProjectOwners(uint256 projectID) external view returns(address[] memory) {
        OwnerList storage owners = projectsOwners[projectID];

        address[] memory list = new address[](owners.count);

        uint256 index = 0;
        address current = owners.list[OWNERS_LIST_SENTINEL];

        if (current == address(0x0)) {
            return list;
        }

        while (current != OWNERS_LIST_SENTINEL) {
            list[index] = current;
            current = owners.list[current];
            index++;
        }

        return list;
    }

    // Internal functions

    /**
     * @notice Create initial OwnerList for passed project
     * @param projectID ID of project
     */
    function initProjectOwners(uint256 projectID) internal {
        OwnerList storage owners = projectsOwners[projectID];

        owners.list[OWNERS_LIST_SENTINEL] = msg.sender;
        owners.list[msg.sender] = OWNERS_LIST_SENTINEL;
        owners.count = 1;
    }

    // Private functions
    // ...
}

