// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/ClonesUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../utils/MetaPtr.sol";
import "./IVotingStrategyFactory.sol";
import "./QuadraticFundingVotingStrategyImplementation.sol";

contract QuadraticFundingVotingStrategyFactory is IVotingStrategyFactory, OwnableUpgradeable {

  address public votingContract;

  uint256 public nonce;

  // --- Event ---

  /// @notice Emitted when a Voting contract is updated
  event VotingContractUpdated(address votingContractAddress);

  /// @notice Emitted when a new Voting is created
  event VotingContractCreated(address indexed votingContractAddress, address indexed votingImplementation);


  /// @notice constructor function which ensure deployer is set as owner
  function initialize() external initializer {
    __Context_init_unchained();
    __Ownable_init_unchained();
  }

  // --- Core methods ---

  /**
   * @notice Allows the owner to update the QuadraticFundingVotingStrategyImplementation.
   * This provides us the flexibility to upgrade QuadraticFundingVotingStrategyImplementation
   * contract while relying on the same QuadraticFundingVotingStrategyFactory to get the list of
   * QuadraticFundingVoting contracts.
   */
  function updateVotingContract(address newVotingContract) external onlyOwner {
    // slither-disable-next-line missing-zero-check
    votingContract = newVotingContract;

    emit VotingContractUpdated(newVotingContract);
  }

  /**
   * @notice Clones QuadraticFundingVotingStrategyImplementation and deploys a contract
   * and emits an event
   */
  function create() external returns (address) {

    nonce++;
    bytes32 salt = keccak256(abi.encodePacked(msg.sender, nonce));
    address clone = ClonesUpgradeable.cloneDeterministic(votingContract, salt);

    emit VotingContractCreated(clone, votingContract);
    QuadraticFundingVotingStrategyImplementation(clone).initialize();

    return clone;
  }
}

// SPDX-License-Identifier: AGPL-3.0-only
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/AddressUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";

import "./IVotingStrategy.sol";

/**
 * Allows voters to cast multiple weighted votes to grants with one transaction
 * This is inspired from BulkCheckout documented over at:
 * https://github.com/gitcoinco/BulkTransactions/blob/master/contracts/BulkCheckout.sol
 *
 * Emits event upon every transfer.
 */
contract QuadraticFundingVotingStrategyImplementation is IVotingStrategy, Initializable, ReentrancyGuardUpgradeable {

  using SafeERC20Upgradeable for IERC20Upgradeable;

  string public constant VERSION = "0.2.1";

  // --- Event ---

  /// @notice Emitted when a new vote is sent
  event Voted(
    address token,                    // voting token
    uint256 amount,                   // voting amount
    address origin,                   // voter origin
    address indexed voter,            // voter address
    address grantAddress,             // grant address
    bytes32 indexed projectId,        // project id
    uint256 applicationIndex,         // application index
    address indexed roundAddress      // round address
  );

  // --- Core methods ---

  function initialize() external initializer {
    // empty initializer
  }

  /**
   * @notice Invoked by RoundImplementation which allows
   * a voted to cast weighted votes to multiple grants during a round
   *
   * @dev
   * - more voters -> higher the gas
   * - this would be triggered when a voter casts their vote via grant explorer
   * - can be invoked by the round
   * - supports ERC20 and Native token transfer
   *
   * @param encodedVotes encoded list of votes
   * @param voterAddress voter address
   */
  function vote(bytes[] calldata encodedVotes, address voterAddress) external override payable nonReentrant isRoundContract {
    uint256 msgValue = 0;
    /// @dev iterate over multiple donations and transfer funds
    for (uint256 i = 0; i < encodedVotes.length; i++) {
      /// @dev decode encoded vote
      (
        address _token,
        uint256 _amount,
        address _grantAddress,
        bytes32 _projectId,
        uint256 _applicationIndex
      ) = abi.decode(encodedVotes[i], (
        address,
        uint256,
        address,
        bytes32,
        uint256
      ));

      if (_token == address(0)) {
        /// @dev native token transfer to grant address
        // slither-disable-next-line reentrancy-events
        msgValue += _amount;
        AddressUpgradeable.sendValue(payable(_grantAddress), _amount);
      } else {
        /// @dev erc20 transfer to grant address
        // slither-disable-next-line arbitrary-send-erc20,reentrancy-events,
        SafeERC20Upgradeable.safeTransferFrom(
          IERC20Upgradeable(_token),
          voterAddress,
          _grantAddress,
          _amount
        );
      }

      /// @dev emit event for transfer
      emit Voted(
        _token,
        _amount,
        tx.origin,
        voterAddress,
        _grantAddress,
        _projectId,
        _applicationIndex,
        msg.sender
      );
    }

    require(msgValue == msg.value, "msg.value does not match vote amount");
  }
}
